import React from "react";

const StatCard = ({ title, value, icon, iconColor }) => {
  return (
    <div className="rounded-lg shadow p-4 flex items-center gap-4 bg-gradient-to-r from-blue-100 via-purple-100 to-pink-100 border border-blue-200">
      <div className="text-4xl drop-shadow" style={{ color: iconColor }}>
        {icon}
      </div>
      <div className="flex flex-col">
        <p className="font-semibold text-gray-700 text-sm mb-1">{title}</p>
        <p className="font-bold text-2xl text-gray-900">{value}</p>
      </div>
    </div>
  );
};

export default StatCard;
