import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";

const CreateAuctionPage = () => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    startingBid: "",
    endTime: "",
    imageUrl: "",
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const { title, description, startingBid, endTime, imageUrl } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError(""); // Clear error when user starts typing
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (
      !title.trim() ||
      !description.trim() ||
      !startingBid ||
      !endTime ||
      !imageUrl.trim()
    ) {
      setError("Please fill in all fields.");
      return;
    }

    if (new Date(endTime) <= new Date()) {
      setError("End date must be in the future.");
      return;
    }

    if (parseInt(startingBid) < 1) {
      setError("Starting bid must be at least ₹1.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const auctionData = {
        ...formData,
        startingBid: parseInt(startingBid),
      };

      const res = await api.post("/auctions", auctionData);
      navigate(`/auctions/${res.data._id}`);
    } catch (err) {
      setError(
        err.response?.data?.msg || "Failed to create auction. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  const getMinDateTime = () => {
    const now = new Date();
    const localDateTime = new Date(
      now.getTime() - now.getTimezoneOffset() * 60000
    );
    return localDateTime.toISOString().slice(0, 16);
  };

  return (
    <div className="w-11/12 max-w-lg mx-auto py-8">
      <div className="bg-gray-800 p-8 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold text-center mb-6 text-white">
          List a New Item
        </h2>
        {error && (
          <p className="bg-red-500 text-white p-2 rounded mb-4 text-center">
            {error}
          </p>
        )}
        <form onSubmit={onSubmit}>
          <div className="mb-4">
            <label className="block mb-1 text-gray-300 font-medium">
              Item Title *
            </label>
            <input
              type="text"
              name="title"
              value={title}
              onChange={onChange}
              className="w-full p-3 bg-gray-700 text-white rounded focus:outline-none focus:ring-2 focus:ring-emerald-400"
              placeholder="Enter item title"
              maxLength="100"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block mb-1 text-gray-300 font-medium">
              Description *
            </label>
            <textarea
              name="description"
              value={description}
              onChange={onChange}
              className="w-full p-3 bg-gray-700 text-white rounded focus:outline-none focus:ring-2 focus:ring-emerald-400"
              placeholder="Describe your item in detail"
              rows="4"
              maxLength="500"
              required
            />
          </div>
          <div className="flex flex-col md:flex-row gap-4 mb-4">
            <div className="flex-1">
              <label className="block mb-1 text-gray-300 font-medium">
                Starting Bid (₹) *
              </label>
              <input
                type="number"
                name="startingBid"
                value={startingBid}
                onChange={onChange}
                className="w-full p-3 bg-gray-700 text-white rounded focus:outline-none focus:ring-2 focus:ring-emerald-400"
                min="1"
                placeholder="1"
                required
              />
            </div>
            <div className="flex-1">
              <label className="block mb-1 text-gray-300 font-medium">
                End Date & Time *
              </label>
              <input
                type="datetime-local"
                name="endTime"
                value={endTime}
                onChange={onChange}
                className="w-full p-3 bg-gray-700 text-white rounded focus:outline-none focus:ring-2 focus:ring-emerald-400"
                min={getMinDateTime()}
                required
              />
            </div>
          </div>
          <div className="mb-4">
            <label className="block mb-1 text-gray-300 font-medium">
              Image URL *
            </label>
            <input
              type="url"
              name="imageUrl"
              value={imageUrl}
              onChange={onChange}
              className="w-full p-3 bg-gray-700 text-white rounded focus:outline-none focus:ring-2 focus:ring-emerald-400"
              placeholder="https://example.com/image.jpg"
              required
            />
            {imageUrl && (
              <div className="mt-2 flex items-center gap-2">
                <img
                  src={imageUrl}
                  alt="Preview"
                  className="h-24 rounded border border-gray-600"
                  onError={(e) => {
                    e.target.style.display = "none";
                    e.target.nextSibling.style.display = "block";
                  }}
                />
                <div
                  className="text-gray-400 text-sm"
                  style={{ display: "none" }}
                >
                  Image not found
                </div>
              </div>
            )}
          </div>
          <button
            type="submit"
            className="w-full py-3 rounded-lg font-bold bg-emerald-500 text-white hover:bg-emerald-600 transition text-lg mt-2"
            disabled={loading}
          >
            {loading ? "Creating Auction..." : "List Item"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default CreateAuctionPage;
