import React from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../../hooks/useAuth";

const Header = () => {
  const { user, logout } = useAuth();

  return (
  <header className="bg-gradient-to-r from-blue-600 via-purple-500 to-pink-500 shadow-lg py-4 px-6 flex items-center justify-between">
  <nav className="flex items-center gap-6 w-full">
        <Link
          to="/"
          className="text-2xl font-extrabold tracking-tight bg-clip-text text-transparent bg-gradient-to-r from-yellow-300 via-pink-200 to-purple-200 drop-shadow"
        >
          BidNow
        </Link>
  <div className="flex items-center gap-4 ml-auto">
          {user ? (
            <>
              <Link
                to="/create"
                className="bg-gradient-to-r from-pink-500 to-purple-500 text-white px-4 py-2 rounded shadow hover:from-pink-600 hover:to-purple-600 transition-colors font-semibold"
              >
                List an Item
              </Link>
              <span className="ml-4 text-white font-medium drop-shadow">Welcome, {user.username}</span>
              <button
                onClick={logout}
                className="bg-gradient-to-r from-red-500 to-pink-500 text-white px-4 py-2 rounded shadow hover:from-red-600 hover:to-pink-600 transition-colors font-semibold"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-white font-semibold hover:underline">Login</Link>
              <Link
                to="/register"
                className="bg-gradient-to-r from-pink-500 to-purple-500 text-white px-4 py-2 rounded shadow hover:from-pink-600 hover:to-purple-600 transition-colors font-semibold"
              >
                Sign Up
              </Link>
            </>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
