import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const Header = () => {
  const { user, logout } = useAuth();

  return (
    <header className="bg-gray-800 py-4 shadow-md">
      <nav className="flex justify-between items-center w-11/12 max-w-6xl mx-auto">
        <Link to="/" className="text-emerald-400 font-bold text-2xl hover:text-emerald-500 transition">
          BidNow
        </Link>
        <div className="flex items-center gap-8 text-lg font-medium">
          {user ? (
            <>
              <Link to="/create" className="px-6 py-3 rounded-lg font-bold bg-emerald-500 text-white hover:bg-emerald-600 transition text-lg">
                List an Item
              </Link>
              <span className="ml-4 text-gray-200">Welcome, {user.username}</span>
              <button onClick={logout} className="px-6 py-3 rounded-lg font-bold bg-red-500 text-white hover:bg-red-600 transition text-lg ml-2">
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-gray-200 hover:text-emerald-400 transition">Login</Link>
              <Link to="/register" className="px-6 py-3 rounded-lg font-bold bg-emerald-500 text-white hover:bg-emerald-600 transition text-lg">
                Sign Up
              </Link>
            </>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
