import React from "react";
import { Link } from "react-router-dom";

const AuctionCard = ({ auction }) => {
  const getTimeLeft = () => {
    const now = new Date();
    const endTime = new Date(auction.endTime);
    const difference = endTime - now;

    if (difference <= 0) return "Ended";

    const hours = Math.floor(difference / (1000 * 60 * 60));
    const days = Math.floor(hours / 24);

    if (days > 0) {
      return `${days}d ${hours % 24}h`;
    } else if (hours > 0) {
      const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
      return `${hours}h ${minutes}m`;
    } else {
      const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
      return `${minutes}m`;
    }
  };

  const formatPrice = (price) => {
    if (price >= 1000000) {
      return `₹${(price / 1000000).toFixed(1)}M`;
    } else if (price >= 1000) {
      return `₹${(price / 1000).toFixed(1)}K`;
    }
    return `₹${price}`;
  };

  return (
    <Link
      to={`/auctions/${auction._id}`}
      className="block bg-gray-800 rounded-xl shadow-lg hover:shadow-2xl hover:-translate-y-1 transition-transform overflow-hidden"
    >
      <div className="p-4 border-b border-gray-700">
        <div className="inline-block bg-emerald-500 text-white text-xs font-bold px-3 py-1 rounded-full mb-2">
          Live
        </div>
        <h3 className="text-xl font-bold text-white mb-1 truncate">
          {auction.title}
        </h3>
        <p className="text-gray-300 text-sm line-clamp-3 mb-2">
          {auction.description}
        </p>
      </div>
      <div className="bg-gray-900 flex items-center justify-center h-48">
        <img
          src={auction.imageUrl}
          alt={auction.title}
          className="max-h-44 object-contain rounded-lg"
        />
      </div>
      <div className="p-4">
        <p className="text-gray-400 text-xs mb-2">
          Sold by: {auction.seller?.username || "Unknown"}
        </p>
        <div className="flex justify-between items-center mt-2">
          <div>
            <p className="text-gray-400 text-xs">Current Bid</p>
            <p className="text-lg font-bold text-emerald-400">
              {formatPrice(auction.currentBid)}
            </p>
          </div>
          <div>
            <p className="text-gray-400 text-xs">Time Left</p>
            <p className="text-lg font-bold text-yellow-400">{getTimeLeft()}</p>
          </div>
        </div>
      </div>
    </Link>
  );
};

export default AuctionCard;
