import React from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../../hooks/useAuth";

const Header = () => {
  const { user, logout } = useAuth();

  return (
    <header className="bg-gray-800">
      <nav className="flex items-center justify-between px-8 py-4">
        <Link to="/" className="text-green-400 font-bold text-xl">
          BidNow
        </Link>
        <div className="flex items-center space-x-4">
          {user ? (
            <>
              <Link
                to="/create"
                className="bg-green-400 text-white px-4 py-2 rounded font-semibold hover:bg-green-500 transition"
              >
                List an Item
              </Link>
              <span className="text-white">Welcome, {user.username}</span>
              <button
                onClick={logout}
                className="bg-red-500 text-white px-4 py-2 rounded font-semibold hover:bg-red-600 transition"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-white">
                Login
              </Link>
              <Link
                to="/register"
                className="bg-green-400 text-white px-4 py-2 rounded font-semibold hover:bg-green-500 transition"
              >
                Sign Up
              </Link>
            </>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
