import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";

const CreateAuctionPage = () => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    startingBid: "",
    endTime: "",
    imageUrl: "",
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const { title, description, startingBid, endTime, imageUrl } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError(""); // Clear error when user starts typing
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (
      !title.trim() ||
      !description.trim() ||
      !startingBid ||
      !endTime ||
      !imageUrl.trim()
    ) {
      setError("Please fill in all fields.");
      return;
    }

    if (new Date(endTime) <= new Date()) {
      setError("End date must be in the future.");
      return;
    }

    if (parseInt(startingBid) < 1) {
      setError("Starting bid must be at least ₹1.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const auctionData = {
        ...formData,
        startingBid: parseInt(startingBid),
      };

      const res = await api.post("/auctions", auctionData);
      navigate(`/auctions/${res.data._id}`);
    } catch (err) {
      setError(
        err.response?.data?.msg || "Failed to create auction. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  const getMinDateTime = () => {
    const now = new Date();
    const localDateTime = new Date(
      now.getTime() - now.getTimezoneOffset() * 60000
    );
    return localDateTime.toISOString().slice(0, 16);
  };

  return (
    <div className="max-w-lg mx-auto mt-10 bg-white p-8 rounded shadow">
      <div className="create-auction-form">
        <h2 className="text-2xl font-bold mb-6">Create Auction</h2>
        {error && <p className="error-message">{error}</p>}

        <form onSubmit={onSubmit}>
          <div className="mb-4">
            <label>Item Title *</label>
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="border rounded px-3 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-400"
              required
            />
          </div>

          <div className="mb-4">
            <label>Description *</label>
            <textarea
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="border rounded px-3 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-400"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Starting Bid (₹) *</label>
              <input
                type="number"
                id="startingBid"
                value={startingBid}
                onChange={(e) => setStartingBid(e.target.value)}
                className="border rounded px-3 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-400"
                required
              />
            </div>
            <div className="form-group">
              <label>End Date & Time *</label>
              <input
                type="datetime-local"
                id="endTime"
                value={endTime}
                onChange={(e) => setEndTime(e.target.value)}
                className="border rounded px-3 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-400"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label>Image URL *</label>
            <input
              type="url"
              id="imageUrl"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
              className="border rounded px-3 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-400"
              required
            />
            {imageUrl && (
              <div className="image-preview">
                <img
                  src={imageUrl}
                  alt="Preview"
                  onError={(e) => {
                    e.target.style.display = "none";
                    e.target.nextSibling.style.display = "block";
                  }}
                />
                <div
                  className="image-preview-placeholder"
                  style={{ display: "none" }}
                >
                  Image not found
                </div>
              </div>
            )}
          </div>

          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors w-full"
          >
            Create Auction
          </button>
        </form>
      </div>
    </div>
  );
};

export default CreateAuctionPage;
