import React from "react";
import { Link } from "react-router-dom";

const AuctionCard = ({ auction }) => {
  const getTimeLeft = () => {
    const now = new Date();
    const endTime = new Date(auction.endTime);
    const difference = endTime - now;

    if (difference <= 0) return "Ended";

    const hours = Math.floor(difference / (1000 * 60 * 60));
    const days = Math.floor(hours / 24);

    if (days > 0) {
      return `${days}d ${hours % 24}h`;
    } else if (hours > 0) {
      const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
      return `${hours}h ${minutes}m`;
    } else {
      const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
      return `${minutes}m`;
    }
  };

  const formatPrice = (price) => {
    if (price >= 1000000) {
      return `₹${(price / 1000000).toFixed(1)}M`;
    } else if (price >= 1000) {
      return `₹${(price / 1000).toFixed(1)}K`;
    }
    return `₹${price}`;
  };

  return (
    <Link
      to={`/auctions/${auction._id}`}
      className="block rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-200 overflow-hidden border border-blue-100 bg-gradient-to-br from-white via-blue-50 to-pink-50 hover:scale-[1.02]"
    >
      <div className="flex flex-col gap-2 p-4 border-b bg-gradient-to-r from-blue-100 via-purple-100 to-pink-100">
        <div className="flex items-center gap-2 mb-1">
          <span className="bg-green-500 text-white text-xs px-2 py-1 rounded-full font-bold shadow">Live</span>
          <h3 className="font-semibold text-lg text-blue-700 truncate">{auction.title}</h3>
        </div>
        <p className="text-gray-600 text-sm truncate">{auction.description}</p>
      </div>

      <div className="w-full h-48 bg-gradient-to-br from-blue-200 via-purple-100 to-pink-200 flex items-center justify-center overflow-hidden">
        <img
          src={auction.imageUrl}
          alt={auction.title}
          className="object-cover w-full h-full rounded-t-xl"
        />
      </div>

      <div className="p-4 bg-white rounded-b-xl">
        <p className="text-xs text-gray-500 mb-2">
          <span className="font-semibold text-gray-700">Sold by:</span> {auction.seller?.username || "Unknown"}
        </p>
        <div className="flex justify-between items-center mt-2">
          <div className="text-center">
            <p className="text-xs text-gray-400">Current Bid</p>
            <p className="font-bold text-green-600 text-lg">
              {formatPrice(auction.currentBid)}
            </p>
          </div>
          <div className="text-center">
            <p className="text-xs text-gray-400">Time Left</p>
            <p className="font-semibold text-blue-600 text-lg">{getTimeLeft()}</p>
          </div>
        </div>
      </div>
    </Link>
  );
};

export default AuctionCard;
