import React, { useState, useEffect } from "react";
import api from "../services/api";
import { useAuth } from "../hooks/useAuth";
import AuctionCard from "../components/auction/AuctionCard";
import StatCard from "../components/ui/StatCard";
import { FaGavel, FaUsers, FaTrophy } from "react-icons/fa";

const HomePage = () => {
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("all");
  const { user } = useAuth();

  useEffect(() => {
    fetchAuctions();
  }, []);

  const fetchAuctions = async () => {
    try {
      const res = await api.get("/auctions");
      console.log("Fetched auctions:", res.data); // Debug log
      setAuctions(res.data);
    } catch (err) {
      console.error("Failed to fetch auctions", err);
    } finally {
      setLoading(false);
    }
  };

  const hasUserParticipated = (auction) => {
    if (!user || !auction.bids) return false;

    // Check if user has any bids in this auction
    return auction.bids.some((bid) => {
      // Handle both string and object formats
      const bidUserId = typeof bid.user === "string" ? bid.user : bid.user?._id;
      return bidUserId === user._id;
    });
  };

  const isUserWinning = (auction) => {
    if (!user || auction.isFinished) return false;

    // Check if user is the highest bidder
    const highestBidderId =
      typeof auction.highestBidder === "string"
        ? auction.highestBidder
        : auction.highestBidder?._id;

    return highestBidderId === user._id;
  };

  const getStats = () => {
    const liveAuctions = auctions.filter((a) => !a.isFinished);
    const participatedAuctions = user
      ? auctions.filter(hasUserParticipated)
      : [];
    const winningAuctions = user ? auctions.filter(isUserWinning) : [];

    return {
      liveAuctions: liveAuctions.length,
      participated: participatedAuctions.length,
      winning: winningAuctions.length,
    };
  };

  const getFilteredAuctions = () => {
    switch (filter) {
      case "live":
        return auctions.filter((a) => !a.isFinished);
      case "participated":
        return user ? auctions.filter(hasUserParticipated) : [];
      case "winning":
        return user ? auctions.filter(isUserWinning) : [];
      default:
        return auctions;
    }
  };

  if (loading)
    return <div className="max-w-5xl mx-auto p-4">Loading auctions...</div>;

  const stats = getStats();
  const filteredAuctions = getFilteredAuctions();

  return (
    <div className="max-w-5xl mx-auto p-4">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4 mb-6">
        <StatCard
          title="Live Auctions"
          value={stats.liveAuctions}
          icon={<FaGavel />}
          iconColor="#60a5fa"
        />
        <StatCard
          title="You Participated"
          value={stats.participated}
          icon={<FaUsers />}
          iconColor="#4ade80"
        />
        <StatCard
          title="You're Winning"
          value={stats.winning}
          icon={<FaTrophy />}
          iconColor="#facc15"
        />
      </div>

      <div className="flex gap-2 mb-6">
        <button
          onClick={() => setFilter("all")}
          className={`px-4 py-2 rounded-full border-2 font-semibold shadow-sm transition-all duration-150 ${
            filter === "all"
              ? "bg-gradient-to-r from-blue-500 to-purple-500 text-white border-blue-500 scale-105"
              : "bg-white text-blue-700 border-blue-200 hover:bg-blue-50"
          }`}
        >
          All ({auctions.length})
        </button>
        <button
          onClick={() => setFilter("live")}
          className={`px-4 py-2 rounded-full border-2 font-semibold shadow-sm transition-all duration-150 ${
            filter === "live"
              ? "bg-gradient-to-r from-green-400 to-blue-400 text-white border-green-400 scale-105"
              : "bg-white text-green-700 border-green-200 hover:bg-green-50"
          }`}
        >
          Live Auctions ({stats.liveAuctions})
        </button>
        {user && (
          <>
            <button
              onClick={() => setFilter("participated")}
              className={`px-4 py-2 rounded-full border-2 font-semibold shadow-sm transition-all duration-150 ${
                filter === "participated"
                  ? "bg-gradient-to-r from-pink-400 to-purple-400 text-white border-pink-400 scale-105"
                  : "bg-white text-pink-700 border-pink-200 hover:bg-pink-50"
              }`}
            >
              You Participated ({stats.participated})
            </button>
            <button
              onClick={() => setFilter("winning")}
              className={`px-4 py-2 rounded-full border-2 font-semibold shadow-sm transition-all duration-150 ${
                filter === "winning"
                  ? "bg-gradient-to-r from-yellow-400 to-pink-400 text-white border-yellow-400 scale-105"
                  : "bg-white text-yellow-700 border-yellow-200 hover:bg-yellow-50"
              }`}
            >
              You're Winning ({stats.winning})
            </button>
          </>
        )}
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {filteredAuctions.length > 0 ? (
          filteredAuctions.map((auction) => (
            <AuctionCard key={auction._id} auction={auction} />
          ))
        ) : (
          <div
            style={{
              gridColumn: "1 / -1",
              textAlign: "center",
              padding: "2rem",
              color: "#9ca3af",
            }}
          >
            No auctions found for this filter.
          </div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
