import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const Header = () => {
  const { user, logout } = useAuth();

  return (
  <header className="bg-white shadow-md py-4 px-6 flex items-center justify-between">
  <nav className="flex items-center gap-6">
  <Link to="/" className="text-2xl font-bold text-blue-600 tracking-tight">
          BidNow
        </Link>
  <div className="flex items-center gap-4">
          {user ? (
            <>
              <Link to="/create" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors">
                List an Item
              </Link>
              <span style={{ marginLeft: '1rem' }}>Welcome, {user.username}</span>
              <button onClick={logout} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition-colors">
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login">Login</Link>
              <Link to="/register" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors">
                Sign Up
              </Link>
            </>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
