import React from 'react';
import { Link } from 'react-router-dom';

const AuctionCard = ({ auction }) => {
  const getTimeLeft = () => {
    const now = new Date();
    const endTime = new Date(auction.endTime);
    const difference = endTime - now;

    if (difference <= 0) return 'Ended';

    const hours = Math.floor(difference / (1000 * 60 * 60));
    const days = Math.floor(hours / 24);

    if (days > 0) {
      return `${days}d ${hours % 24}h`;
    } else if (hours > 0) {
      const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
      return `${hours}h ${minutes}m`;
    } else {
      const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
      return `${minutes}m`;
    }
  };

  const formatPrice = (price) => {
    if (price >= 1000000) {
      return `₹${(price / 1000000).toFixed(1)}M`;
    } else if (price >= 1000) {
      return `₹${(price / 1000).toFixed(1)}K`;
    }
    return `₹${price}`;
  };

  return (
  <Link to={`/auctions/${auction._id}`} className="block bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200 overflow-hidden">
  <div className="flex items-center justify-between p-4 border-b">
  <div className="bg-green-500 text-white text-xs px-2 py-1 rounded">Live</div>
  <h3 className="font-semibold text-lg truncate">{auction.title}</h3>
  <p className="text-gray-600 text-sm truncate">{auction.description}</p>
      </div>

  <div className="w-full h-48 bg-gray-100 flex items-center justify-center overflow-hidden">
        <img 
          src={auction.imageUrl} 
          alt={auction.title} 
          className="object-cover w-full h-full" 
        />
      </div>

  <div className="p-4">
  <p className="text-xs text-gray-500 mb-2">Sold by: {auction.seller?.username || 'Unknown'}</p>

  <div className="flex justify-between items-center mt-2">
          <div className="text-center">
            <p className="text-xs text-gray-400">Current Bid</p>
            <p className="font-bold text-green-600">{formatPrice(auction.currentBid)}</p>
          </div>
          <div className="text-center">
            <p className="text-xs text-gray-400">Time Left</p>
            <p className="font-semibold text-blue-600">{getTimeLeft()}</p>
          </div>
        </div>
      </div>
    </Link>
  );
};

export default AuctionCard;
