import React, { useState } from 'react';
import { useAuth } from '../hooks/useAuth';
import { useNavigate, Link } from 'react-router-dom';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await login(email, password);
      navigate('/');
    } catch (err) {
      setError('Failed to login. Please check your credentials.');
    }
  };

  return (
    <div className="bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-md mx-auto mt-10">
      <h2 className="text-2xl font-bold text-center mb-6 text-white">Login</h2>
      {error && <p className="bg-red-500 text-white p-2 rounded mb-4 text-center">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="email" className="block mb-1 text-gray-300 font-medium">Email</label>
          <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} className="w-full p-3 bg-gray-700 text-white rounded focus:outline-none focus:ring-2 focus:ring-emerald-400" required />
        </div>
        <div className="mb-6">
          <label htmlFor="password" className="block mb-1 text-gray-300 font-medium">Password</label>
          <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} className="w-full p-3 bg-gray-700 text-white rounded focus:outline-none focus:ring-2 focus:ring-emerald-400" required />
        </div>
        <button type="submit" className="w-full py-3 rounded-lg font-bold bg-emerald-500 text-white hover:bg-emerald-600 transition text-lg">Login</button>
      </form>
      <p className="mt-4 text-gray-300 text-center">Don't have an account? <Link to="/register" className="text-emerald-400 hover:underline">Sign up</Link></p>
    </div>
  );
};

export default LoginPage;
