import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { useAuth } from '../hooks/useAuth';
import AuctionCard from '../components/auction/AuctionCard';
import StatCard from '../components/ui/StatCard';
import { FaGavel, FaUsers, FaTrophy } from 'react-icons/fa';

const HomePage = () => {
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const { user } = useAuth();

  useEffect(() => {
    fetchAuctions();
  }, []);

  const fetchAuctions = async () => {
    try {
      const res = await api.get('/auctions');
      console.log('Fetched auctions:', res.data); // Debug log
      setAuctions(res.data);
    } catch (err) {
      console.error("Failed to fetch auctions", err);
    } finally {
      setLoading(false);
    }
  };

  const hasUserParticipated = (auction) => {
    if (!user || !auction.bids) return false;

    // Check if user has any bids in this auction
    return auction.bids.some(bid => {
      // Handle both string and object formats
      const bidUserId = typeof bid.user === 'string' ? bid.user : bid.user?._id;
      return bidUserId === user._id;
    });
  };

  const isUserWinning = (auction) => {
    if (!user || auction.isFinished) return false;

    // Check if user is the highest bidder
    const highestBidderId = typeof auction.highestBidder === 'string' 
      ? auction.highestBidder 
      : auction.highestBidder?._id;

    return highestBidderId === user._id;
  };

  const getStats = () => {
    const liveAuctions = auctions.filter(a => !a.isFinished);
    const participatedAuctions = user ? auctions.filter(hasUserParticipated) : [];
    const winningAuctions = user ? auctions.filter(isUserWinning) : [];

    return {
      liveAuctions: liveAuctions.length,
      participated: participatedAuctions.length,
      winning: winningAuctions.length,
    };
  };

  const getFilteredAuctions = () => {
    switch (filter) {
      case 'live':
        return auctions.filter(a => !a.isFinished);
      case 'participated':
        return user ? auctions.filter(hasUserParticipated) : [];
      case 'winning':
        return user ? auctions.filter(isUserWinning) : [];
      default:
        return auctions;
    }
  };

  if (loading) return <div className="w-11/12 max-w-6xl mx-auto py-8 text-center text-lg text-gray-200">Loading auctions...</div>;

  const stats = getStats();
  const filteredAuctions = getFilteredAuctions();

  return (
    <div className="w-11/12 max-w-6xl mx-auto py-8">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mb-8">
        <StatCard 
          title="Live Auctions" 
          value={stats.liveAuctions} 
          icon={<FaGavel />} 
          iconColor="#60a5fa" 
        />
        <StatCard 
          title="You Participated" 
          value={stats.participated} 
          icon={<FaUsers />} 
          iconColor="#4ade80" 
        />
        <StatCard 
          title="You're Winning" 
          value={stats.winning} 
          icon={<FaTrophy />} 
          iconColor="#facc15" 
        />
      </div>

      <div className="bg-gray-800 p-4 rounded-lg mb-8 flex gap-4 flex-wrap">
        <button 
          onClick={() => setFilter('all')} 
          className={`px-4 py-2 rounded font-semibold bg-gray-700 text-gray-200 hover:bg-emerald-500 hover:text-white transition ${filter === 'all' ? 'bg-emerald-500 text-white' : ''}`}
        >
          All ({auctions.length})
        </button>
        <button 
          onClick={() => setFilter('live')} 
          className={`px-4 py-2 rounded font-semibold bg-gray-700 text-gray-200 hover:bg-emerald-500 hover:text-white transition ${filter === 'live' ? 'bg-emerald-500 text-white' : ''}`}
        >
          Live Auctions ({stats.liveAuctions})
        </button>
        {user && (
          <>
            <button 
              onClick={() => setFilter('participated')} 
              className={`px-4 py-2 rounded font-semibold bg-gray-700 text-gray-200 hover:bg-emerald-500 hover:text-white transition ${filter === 'participated' ? 'bg-emerald-500 text-white' : ''}`}
            >
              You Participated ({stats.participated})
            </button>
            <button 
              onClick={() => setFilter('winning')} 
              className={`px-4 py-2 rounded font-semibold bg-gray-700 text-gray-200 hover:bg-emerald-500 hover:text-white transition ${filter === 'winning' ? 'bg-emerald-500 text-white' : ''}`}
            >
              You're Winning ({stats.winning})
            </button>
          </>
        )}
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredAuctions.length > 0 ? (
          filteredAuctions.map(auction => (
            <AuctionCard key={auction._id} auction={auction} />
          ))
        ) : (
          <div className="col-span-full text-center py-8 text-gray-400">
            No auctions found for this filter.
          </div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
