import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { useAuth } from '../hooks/useAuth';
import AuctionCard from '../components/auction/AuctionCard';
import StatCard from '../components/ui/StatCard';
import { FaGavel, FaClock, FaExclamationTriangle, FaTrophy } from 'react-icons/fa';

const HomePage = () => {
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const { user } = useAuth();

  useEffect(() => {
    fetchAuctions();
  }, []);

  const fetchAuctions = async () => {
    try {
      const res = await api.get('/auctions');
      setAuctions(res.data);
    } catch (err) {
      console.error("Failed to fetch auctions", err);
    } finally {
      setLoading(false);
    }
  };

  const getStats = () => {
    const now = new Date();
    const endingSoon = auctions.filter(a => (new Date(a.endTime) - now) / (1000 * 60 * 60) <= 24);
    const winning = user ? auctions.filter(a => a.highestBidder?._id === user?._id) : [];

    return {
      total: auctions.length,
      active: auctions.filter(a => !a.isFinished).length,
      endingSoon: endingSoon.length,
      winning: winning.length,
    };
  };

  const getFilteredAuctions = () => {
    const now = new Date();
    switch (filter) {
      case 'ending':
        return auctions.filter(a => (new Date(a.endTime) - now) / (1000 * 60 * 60) <= 24);
      case 'winning':
        return user ? auctions.filter(a => a.highestBidder?._id === user?._id) : [];
      default:
        return auctions;
    }
  };

  if (loading) return <div className="container">Loading auctions...</div>;

  const stats = getStats();
  const filteredAuctions = getFilteredAuctions();

  return (
    <div className="container">
      <div className="stats-grid">
        <StatCard title="Total Auctions" value={stats.total} icon={<FaGavel />} iconColor="#60a5fa" />
        <StatCard title="Active" value={stats.active} icon={<FaClock />} iconColor="#4ade80" />
        <StatCard title="Ending Soon" value={stats.endingSoon} icon={<FaExclamationTriangle />} iconColor="#facc15" />
        <StatCard title="You're Winning" value={stats.winning} icon={<FaTrophy />} iconColor="#34d399" />
      </div>

      <div className="filter-tabs">
        <button onClick={() => setFilter('all')} className={`filter-btn ${filter === 'all' ? 'active' : ''}`}>
          All ({stats.total})
        </button>
        <button onClick={() => setFilter('ending')} className={`filter-btn ${filter === 'ending' ? 'active' : ''}`}>
          Ending Soon ({stats.endingSoon})
        </button>
        {user && (
          <button onClick={() => setFilter('winning')} className={`filter-btn ${filter === 'winning' ? 'active' : ''}`}>
            Winning ({stats.winning})
          </button>
        )}
      </div>

      <div className="auction-grid">
        {filteredAuctions.map(auction => (
          <AuctionCard key={auction._id} auction={auction} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
